import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [Image, setImage] = useState({});
  const [query, setQuery] = useState("");

  const getColors = (event) => {
    fetch("/api/index", {method: "POST", body: JSON.stringify({query: query}) }) //get data
      .then((data) =>{setImage(data.colors);})
      var image_link = Image["colors"];
    return image_link
  }

  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" action="/" method="POST" onSubmit={getColors}>
          <input type="text" name="search"></input>
          <input type="submit" value="detect"></input>
        </form>
    </div>
  );
}

export default App;
