import React, {useState} from 'react';
import './App.css';

function App() {
  const [imageLink, setImageLink] = useState({});
  const [query, setQuery] = useState("");

  const getImage = (event) => {
    fetch("/api/index", {method: "POST", cache: "no-cache", headers: {content_type: "application/json",}, body: JSON.stringify({query: query}),})
    .then(
      (res) => {
        return res.json();
        } 
      )
    .then(
      (data) => {
        setImageLink(data.image);
        }
      );
  };

  /*function displayImage() {
    var printImage = document.getElementById(imageLink[0]);
  }*/

  const displayImage = (imageLink) => {
    let img = imageLink["image"];

    return (
      document.getElementById(img)
      //<div class = "Color"></div>
    )
  }

  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" onSubmit={getImage}>
          <input type="text" name="search" value={query} onChange={(event) => setQuery(event.target.value)}></input>
          <input type="submit" value="detect"></input>
        </form>
        {displayImage}
    </div>
  );
}

export default App;
