import React, {useState} from 'react';
import './App.css';

//i get an input here in html (string) that goes to api
// it goes to python, it returns to js as a json file

//goal is to convert it to a string and display it on html
//i get a json file from api py
//i convert it to a strong w/ the string thingy, display it through javascript

function App() {
  const [img, setImg] = useState("");
  const [query, setQuery] = useState("");

  var header = new Headers();
  header.append('Accept', 'application/json'); // This one is enough for GET requests
  header.append('Content-Type', 'application/json'); // This one sends body

  const getImage = (event) => {
    fetch('/api/index', {method: 'POST', cache: 'no-cache', headers: header, body: JSON.stringify({query: query})})
    .then((res) => res.json())
    .then((data) => setImg(data.img))
    .catch((error) => console.error('Error', error));
  }


  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" onSubmit={getImage} method="POST">
          <input value={query}
          onChange={(event) => setQuery(event.target.value)} type="text" name="search"></input>
          <input type="submit" value="detect"></input>
        </form>
        {detectColor}
    </div>
  );
}

export default App;
