import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [imageLink, setImageLink] = useState({});
  const [search, setSearch] = useState("");

  const getImage = (event) => {

    //const [query, setQuery] = useState("");
    //var image_link = json.stringify();
    fetch("/api/index", {method: "POST", cache: "no-cache", headers: {content_type: "application/json",}, body: JSON.stringify({search: search}),})
    .then(
      (res) => {
        return res.json();
        } 
      )
    .then(
      (data) => {
        setImageLink(data.image);
        }
      );
  };

  function displayImage() {
    var printImage = document.getElementById(imageLink[0]);
  }

  /*const displayImage = (imageLink) => {
    let link = imageLink["image"];

    return (
      <div class = "Color"><a href={link} target="_blank">testing!!</a></div>
    )
  }*/

  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" onSubmit={getImage}>
          <input type="text" name="search" value={search} onChange={(event) => setSearch(event.target.value)}></input>
          <input type="submit" value="detect"></input>
        </form>
        {displayImage}
    </div>
  );
}

export default App;
