import React, {useState} from 'react';
import './App.css';

//i get an input here in html (string) that goes to api
// it goes to python, it returns to js as a json file

//goal is to convert it to a string and display it on html
//i get a json file from api py
//i convert it to a strong w/ the string thingy, display it through javascript

function App() {
  //const [imgLink, setImgLink] = useState({});
  //const [query, setQuery] = useState("");

  const getImage = (event) => {

    fetch('api/get_link', {method: 'POST', cache: 'no-cache', headers: {content_type: "application/x-www-form-urlencoded",}, })
      .then((res) => {
        return res.json();
        //json data will arrive here
      })
      .then((data) => {
        displayLink(data);
      });
  }

  function displayLink(data){
    var mainContainer = document.getElementByID("Text");
    var div = document.createElement("div");
    div.innerHTML = 'Link: ' + data[0].image;
    mainContainer.appendChild(div);

  }

  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div id="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
        <form className="Input-Container" onsubmit={getImage}>
          <input type="text" name="search"></input>
          <input type="submit" value="detect"></input>
        </form>
        {displayLink}
      </div>

    </div>
  );
}

export default App;
