import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [Colors, setColors] = useState({});
  //const that recieves info from api.py (construct?)

  const getColors = (event) => {
    fetch("/api/index", {method: "POST"})
  }

  //function that takes items and replaces text portion of page upon clicking submit 


  return (
    <div className="App">
      <div class="Detector-Container">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React Native/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" action="/" method="POST" onSubmit={getColors}>
          <input type="text" id="search" name="search"></input>
          <input type="submit" value="detect"></input>
        </form>
    </div>
    </div>
  );
}

export default App;
