import React, {useState} from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  const [Image, setImage] = useState({});
  const [query, setQuery] = useState("");

  const getColors = (event) => {
    event.preventDefault();
    fetch("/api/index", {method: "POST",  headers: {
      content_type: "application/json",
    },body: JSON.stringify({query: query}) }) //get data
    .then((res) => {
      // console.log(res);
      return res.json();
    })
      .then((data) =>{setImage(data.colors);})
  }

  const returnColors = (Image) => {
    let img = Image["colors"];

    return (<div class="Color"><a href={img}>this is a test</a></div>)
  }

  return (
    <div className="App">
      <div class="Title"><i>Color Detector</i></div>
      <div class="Text">
        <ul>
        <li>App made with React/Flask/Python, hosted on AWS</li>
        <li>Enter a word and an image will be generated, along with five prominent colors.</li>
        </ul>
      </div>
        <form className="Input-Container" action="/api/index" method="POST" onSubmit={getColors}>
          <input type="text" name="search" onChange={(event) => setQuery(event.target.value)}></input>
          <input type="submit" value="detect"></input>
        </form>
        {returnColors}
    </div>
  );
}

export default App;
